```java
public class TestNamePointcut {
    // 只能在本切面类中使用
    @Pointcut("within(com.smart.*)")
    private void inPackage(){}

    // 在切面类、子切面类中使用
    @Pointcut("execution(* greetTo(..)))")
    protected void greetTo(){}

    // 公共使用
    @Pointcut("inPackage() and greetTo()")
    public void inPkgGreetTo(){}
}
