/**
 * 浅复制
 * 1997,英国的克隆羊，多利！
 * 实现 Cloneable接口
 */
public class DeepCopySheep implements Cloneable,Serializable {

	private String sname;

	private Date birthday;

	/**
	 * 直接调用object对象的clone()方法！
	 * @return
	 * @throws CloneNotSupportedException
	 */
	@Override
	protected Object clone() throws CloneNotSupportedException {
		Object obj = super.clone();
		return obj;
	}


	public String getSname() {
		return sname;
	}


	public void setSname(String sname) {
		this.sname = sname;
	}


	public Date getBirthday() {
		return birthday;
	}


	public void setBirthday(Date birthday) {
		this.birthday = birthday;
	}


	public DeepCopySheep(String sname, Date birthday) {
		super();
		this.sname = sname;
		this.birthday = birthday;
	}

	public DeepCopySheep() {
	}

}

/**
 * 深复制
 * 1997,英国的克隆羊，多利！
 */
public class ShallowCopySheep implements Cloneable {
	private String sname;
	private Date birthday;

	/**
	 * 直接调用object对象的clone()方法！
	 * @return
	 * @throws CloneNotSupportedException
	 */
	@Override
	protected Object clone() throws CloneNotSupportedException {
		Object obj = super.clone();
		// 添加如下代码实现深复制(deep Clone)
		ShallowCopySheep s = (ShallowCopySheep) obj;
		// 把属性也进行克隆！
		s.birthday = (Date) this.birthday.clone();

		return obj;
	}


	public String getSname() {
		return sname;
	}


	public void setSname(String sname) {
		this.sname = sname;
	}


	public Date getBirthday() {
		return birthday;
	}


	public void setBirthday(Date birthday) {
		this.birthday = birthday;
	}


	public ShallowCopySheep(String sname, Date birthday) {
		super();
		this.sname = sname;
		this.birthday = birthday;
	}

	public ShallowCopySheep() {
	}

}

