        if (address.length == 1) {
            province = address[0];
            city = address[0];
        }
        if (address.length == 2) {
            province = address[0];
            city = address[1];
        }
        map.put("province", province);
        map.put("city", city);
        logger.info(mobile + " 手机号所在信息:" + map);
        return map;
    }

    public static String getMobileAddressUtils(String mobile){
        try {
            String url = "http://www.ip138.com:8080/search.asp?action=mobile&mobile=%s";
